import axios from './axios';
import { auth } from "./auth";
import { findApiByName } from "./apiUtil";

const user= {
    fullName: '',
    email: '',
    phone: '',
    role: '',
}

 
export default async function loadInfor() {
    try {
      const apiObject = findApiByName("customer", "common").url;
      console.log(apiObject);
      const response = await axios.get(apiObject+"/"+ auth());
      
      if(response.status == 200){
        user.fullname = response.data.data.fullName;
        user.email = response.data.data.email;
        user.phone = response.data.data.phone;
        user.role = response.data.data.user.role;

        return user;
      }

    } catch (error) {
      console.error('Get infor failed', error.response.data);
    }
}
