import axios from './axios';
import { auth } from "./auth";
import { findApiByName } from "./apiUtil";

const user= {
    fullname: '',
    email: '',
    phone: '',
    role: '',
}

async function loadInfor() {
      const apiObject = findApiByName("customer", "common").url;
      console.log(apiObject);
      const response = await axios.get(apiObject+"/"+ auth());
      
      if(response.status == 200){
        console.log(response.data.data);
        this.user.fullname = response.data.data.fullname;
        this.user.email = response.data.data.email;
        this.user.phone = response.data.data.phone;
        this.user.role = response.data.data.user.role;
      }
   

    
}
loadInfor();
export default user;
